import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
    return (
      <button className="square" onClick={props.onClick}>
        {props.value}
      </button>
    );
  }

  class Board extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        squares: Array(64).fill(null),
        xIsNext: true,
      };
    }

    handleClick(i) {
      const squares = this.state.squares.slice();
      if (calculateWinner(squares) || squares[i]) {
        return;
      }
      squares[i] = this.state.xIsNext ? 'X' : 'O';
      this.setState({
        squares: squares,
        xIsNext: !this.state.xIsNext,
      });
    }

    renderSquare(i) {
      return (
        <Square
          value={this.state.squares[i]}
          onClick={() => this.handleClick(i)}
        />
      );
    }

    render() {

    status = 'Next player: ' + (this.state.xIsNext ? 'White' : 'Black');


      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
            {this.renderSquare(6)}
            {this.renderSquare(7)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+8)}
            {this.renderSquare(1+8)}
            {this.renderSquare(2+8)}
            {this.renderSquare(4+8)}
            {this.renderSquare(5+8)}
            {this.renderSquare(6+8)}
            {this.renderSquare(7+8)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+16)}
            {this.renderSquare(1+16)}
            {this.renderSquare(2+16)}
            {this.renderSquare(4+16)}
            {this.renderSquare(5+16)}
            {this.renderSquare(6+16)}
            {this.renderSquare(7+16)}
          </div>          
          <div className="board-row">
            {this.renderSquare(0+24)}
            {this.renderSquare(1+24)}
            {this.renderSquare(2+24)}
            {this.renderSquare(4+24)}
            {this.renderSquare(5+24)}
            {this.renderSquare(6+24)}
            {this.renderSquare(7+24)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+32)}
            {this.renderSquare(1+32)}
            {this.renderSquare(2+32)}
            {this.renderSquare(4+32)}
            {this.renderSquare(5+32)}
            {this.renderSquare(6+32)}
            {this.renderSquare(7+32)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+40)}
            {this.renderSquare(1+40)}
            {this.renderSquare(2+40)}
            {this.renderSquare(4+40)}
            {this.renderSquare(5+40)}
            {this.renderSquare(6+40)}
            {this.renderSquare(7+40)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+48)}
            {this.renderSquare(1+48)}
            {this.renderSquare(2+48)}
            {this.renderSquare(4+48)}
            {this.renderSquare(5+48)}
            {this.renderSquare(6+48)}
            {this.renderSquare(7+48)}
          </div>
          <div className="board-row">
            {this.renderSquare(0+56)}
            {this.renderSquare(1+56)}
            {this.renderSquare(2+56)}
            {this.renderSquare(4+56)}
            {this.renderSquare(5+56)}
            {this.renderSquare(6+56)}
            {this.renderSquare(7+56)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );

  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }
